import React, { useEffect, useState } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableRow from '@mui/material/TableRow';
import TableHead from '@mui/material/TableHead';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';

const teamColors = {
  'Red Bull Racing': { primary: '#3671C6', secondary: '#1E41C3' },
  'Mercedes': { primary: '#6CD3BF', secondary: '#00D2BE' },
  'McLaren': { primary: '#FF8700', secondary: '#FF5800' },
  'Ferrari': { primary: '#DC0000', secondary: '#B80000' },
  'Aston Martin': { primary: '#358C75', secondary: '#006F62' },
  'Alpine': { primary: '#0090FF', secondary: '#0051FF' },
  'Stake F1 Team Kick Sauber': { primary: '#52E252', secondary: '#37BEDD' },
  'Haas': { primary: '#FFFFFF', secondary: '#E8E8E8' },
  'Williams': { primary: '#37BEDD', secondary: '#005AFF' },
  'Visa Cash App RB': { primary: '#5E8FAA', secondary: '#1E41C3' }
};

export default function ProfilePage() {
  const { driver_id } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedGP, setSelectedGP] = useState(0);

  // Obtener league_id de la query
  const league_id = new URLSearchParams(location.search).get('league_id');
  const player_id = localStorage.getItem('player_id');

  useEffect(() => {
    const fetchProfile = async () => {
      setLoading(true);
      const res = await fetch(`/api/players/${player_id}/drivers/${driver_id}/profile?league_id=${league_id}`);
      const data = await res.json();
      setProfile(data);
      setLoading(false);
    };
    if (player_id && driver_id && league_id) fetchProfile();
  }, [player_id, driver_id, league_id]);

  if (loading || !profile) return (
    <Box sx={{ minHeight: '100vh', background: '#111', color: '#fff', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <Typography>Cargando perfil...</Typography>
    </Box>
  );

  const { driver, calendar, criteria, value, clausula, is_owned } = profile;
  const teamColor = teamColors[driver?.team] || { primary: '#666', secondary: '#444' };

  return (
    <Box sx={{ minHeight: '100vh', background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%)', color: '#fff', p: 0 }}>
      <Box sx={{ maxWidth: 500, mx: 'auto', mt: 4, p: 3, borderRadius: 4, background: 'linear-gradient(135deg, #1a1a1a 0%, #232323 100%)', boxShadow: 6, border: `2px solid ${teamColor.primary}`, position: 'relative' }}>
        <IconButton onClick={() => navigate(-1)} sx={{ position: 'absolute', top: 12, right: 12, color: '#fff', background: 'rgba(0,0,0,0.3)' }}>
          <CloseIcon />
        </IconButton>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Avatar src={driver?.image_url ? `/images/${driver.image_url}` : ''} sx={{ width: 64, height: 64, border: `3px solid ${teamColor.primary}`, mr: 2 }} />
          <Box>
            <Typography variant="h5" sx={{ fontWeight: 700, color: teamColor.primary }}>{driver.driver_name}</Typography>
            <Typography variant="body2" sx={{ color: '#b0b0b0', fontWeight: 500 }}>{driver.team}</Typography>
            <Typography variant="body2" sx={{ color: '#4CAF50', fontWeight: 700 }}>{(value || 0).toLocaleString()} €</Typography>
            {/* Clausula */}
            {clausula && clausula.length > 0 ? (
              <Box sx={{ mt: 1 }}>
                <Typography variant="body2" sx={{ color: '#FFD600', fontWeight: 700 }}>Cláusula:</Typography>
                {clausula.map((c, idx) => (
                  <Box key={idx} sx={{ ml: 1, mb: 0.5 }}>
                    <Typography variant="body2" sx={{ color: c.active ? '#4CAF50' : '#F44336', fontWeight: 600 }}>
                      {c.active ? 'Activa' : 'Inactiva'} - Premio: {(c.prize || 0).toLocaleString()} € - Expira: {c.expiration_date ? new Date(c.expiration_date).toLocaleDateString() : 'N/A'}
                    </Typography>
                  </Box>
                ))}
              </Box>
            ) : (
              <Typography variant="body2" sx={{ color: '#b0b0b0', fontWeight: 500, mt: 1 }}>Sin cláusula</Typography>
            )}
          </Box>
        </Box>
        {/* Barra de Grandes Premios */}
        <Box sx={{ display: 'flex', overflowX: 'auto', mb: 2, gap: 1 }}>
          {calendar.map((gp, idx) => (
            <Box
              key={gp.name}
              onClick={() => setSelectedGP(idx)}
              sx={{
                minWidth: 48,
                px: 1,
                py: 0.5,
                borderRadius: 2,
                background: selectedGP === idx ? teamColor.primary : '#232323',
                color: selectedGP === idx ? '#fff' : '#b0b0b0',
                textAlign: 'center',
                cursor: 'pointer',
                fontWeight: 700,
                border: selectedGP === idx ? `2px solid ${teamColor.secondary}` : '2px solid transparent',
                fontSize: '0.95rem',
                boxShadow: selectedGP === idx ? '0 2px 8px rgba(0,0,0,0.3)' : 'none'
              }}
            >
              {gp.name.split(' ')[0]}
            </Box>
          ))}
        </Box>
        {/* Tabla de criterios y puntos */}
        <Box sx={{ mt: 2 }}>
          <Typography variant="h6" sx={{ color: teamColor.primary, fontWeight: 700, mb: 1 }}>Criterios de Puntuación</Typography>
          <Table size="small" sx={{ background: '#181818', borderRadius: 2 }}>
            <TableHead>
              <TableRow>
                <TableCell sx={{ color: '#fff', fontWeight: 700, background: '#232323' }}>Criterio</TableCell>
                <TableCell sx={{ color: '#fff', fontWeight: 700, background: '#232323' }}>Puntos</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {criteria.map((crit, idx) => (
                <TableRow key={crit.label}>
                  <TableCell sx={{ color: '#b0b0b0', fontWeight: 500 }}>{crit.label}</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700 }}>{Array.isArray(crit.value) ? (crit.value[selectedGP] || 0) : crit.value}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Box>
      </Box>
    </Box>
  );
}
