import React, { useEffect, useState } from 'react';
import { Box, Typography, Button, MenuItem, Select, TextField, FormControl, InputLabel, Snackbar, Alert } from '@mui/material';
import { useNavigate } from 'react-router-dom';

export default function AdminScoresPage() {
  const [pilots, setPilots] = useState([]);
  const [gps, setGps] = useState([]);
  const [sessionType, setSessionType] = useState('race');
  const [pilotId, setPilotId] = useState('');
  const [gpIndex, setGpIndex] = useState('');
  const [fields, setFields] = useState({});
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const navigate = useNavigate();

  useEffect(() => {
    // Cargar pilotos y GPs
    fetch('/api/pilots').then(res => res.json()).then(data => setPilots(data.pilots || []));
    fetch('/api/grand-prix').then(res => res.json()).then(data => setGps(data.gps || []));
  }, []);

  const handleFieldChange = (e) => {
    setFields({ ...fields, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let endpoint = '';
    if (sessionType === 'race') endpoint = '/api/admin/pilot-race';
    if (sessionType === 'qualy') endpoint = '/api/admin/pilot-qualy';
    if (sessionType === 'practice') endpoint = '/api/admin/pilot-practice';
    const res = await fetch(endpoint, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ pilot_id: pilotId, gp_index: gpIndex, ...fields })
    });
    const data = await res.json();
    if (res.ok) {
      setSnackbar({ open: true, message: 'Puntuación guardada', severity: 'success' });
    } else {
      setSnackbar({ open: true, message: data.error || 'Error al guardar', severity: 'error' });
    }
  };

  // 1. Estilos para mejor contraste en hover/focus
  const selectSx = {
    color: '#fff',
    background: '#23243a',
    '.MuiSvgIcon-root': { color: '#fff' },
    '.MuiSelect-icon': { color: '#fff' },
    '&:hover': { background: '#222' },
    '&.Mui-focused': { background: '#333' },
  };
  const menuItemSx = { color: '#fff', background: '#23243a', '&:hover': { background: '#333' } };

  // 2. Labels con distinción de modo
  const sessionFields = {
    race: [
      { name: 'start_position', label: 'Posición de salida (Carrera)', type: 'number' },
      { name: 'finish_position', label: 'Posición final (Carrera)', type: 'number' },
      { name: 'expected_position', label: 'Posición esperada (Carrera)', type: 'number' },
      { name: 'delta_position', label: 'Delta posición (Carrera)', type: 'number' },
      { name: 'points', label: 'Puntos (Carrera)', type: 'number' },
      { name: 'positions_gained_at_start', label: 'Posiciones ganadas en salida (Carrera)', type: 'number' },
      { name: 'clean_overtakes', label: 'Adelantamientos limpios (Carrera)', type: 'number' },
      { name: 'net_positions_lost', label: 'Posiciones netas perdidas (Carrera)', type: 'number' },
      { name: 'fastest_lap', label: 'Vuelta rápida (Carrera)', type: 'checkbox' },
      { name: 'caused_vsc', label: 'Causó VSC (Carrera)', type: 'checkbox' },
      { name: 'caused_sc', label: 'Causó SC (Carrera)', type: 'checkbox' },
      { name: 'caused_red_flag', label: 'Causó bandera roja (Carrera)', type: 'checkbox' },
      { name: 'dnf_driver_error', label: 'DNF error piloto (Carrera)', type: 'checkbox' },
      { name: 'dnf_no_fault', label: 'DNF sin culpa (Carrera)', type: 'checkbox' },
    ],
    qualy: [
      { name: 'start_position', label: 'Posición de salida (Qualy)', type: 'number' },
      { name: 'finish_position', label: 'Posición final (Qualy)', type: 'number' },
      { name: 'expected_position', label: 'Posición esperada (Qualy)', type: 'number' },
      { name: 'delta_position', label: 'Delta posición (Qualy)', type: 'number' },
      { name: 'points', label: 'Puntos (Qualy)', type: 'number' },
      { name: 'caused_red_flag', label: 'Causó bandera roja (Qualy)', type: 'checkbox' },
    ],
    practice: [
      { name: 'start_position', label: 'Posición de salida (Práctica)', type: 'number' },
      { name: 'finish_position', label: 'Posición final (Práctica)', type: 'number' },
      { name: 'expected_position', label: 'Posición esperada (Práctica)', type: 'number' },
      { name: 'delta_position', label: 'Delta posición (Práctica)', type: 'number' },
      { name: 'points', label: 'Puntos (Práctica)', type: 'number' },
      { name: 'caused_red_flag', label: 'Causó bandera roja (Práctica)', type: 'checkbox' },
    ]
  };

  return (
    <Box sx={{ maxWidth: 500, mx: 'auto', mt: 4, p: 3, background: '#181818', borderRadius: 3 }}>
      <Typography variant="h5" sx={{ mb: 2, color: '#fff' }}>Administrar Puntuaciones Manuales</Typography>
      <form onSubmit={handleSubmit}>
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel sx={{ color: '#fff' }}>Piloto</InputLabel>
          <Select value={pilotId} label="Piloto" onChange={e => setPilotId(e.target.value)} required sx={selectSx}>
            {pilots.map(p => <MenuItem key={p.id} value={p.id} sx={menuItemSx}>{p.driver_name}</MenuItem>)}
          </Select>
        </FormControl>
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel sx={{ color: '#fff' }}>Gran Premio</InputLabel>
          <Select value={gpIndex} label="Gran Premio" onChange={e => setGpIndex(e.target.value)} required sx={selectSx}>
            {gps.map(gp => <MenuItem key={gp.gp_index} value={gp.gp_index} sx={menuItemSx}>{gp.name}</MenuItem>)}
          </Select>
        </FormControl>
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel sx={{ color: '#fff' }}>Sesión</InputLabel>
          <Select value={sessionType} label="Sesión" onChange={e => setSessionType(e.target.value)} required sx={selectSx}>
            <MenuItem value="race" sx={menuItemSx}>Carrera</MenuItem>
            <MenuItem value="qualy" sx={menuItemSx}>Clasificación</MenuItem>
            <MenuItem value="practice" sx={menuItemSx}>Práctica</MenuItem>
          </Select>
        </FormControl>
        {sessionFields[sessionType].map(field => (
          <Box key={field.name} sx={{ mb: 2 }}>
            {field.type === 'checkbox' ? (
              <FormControl fullWidth>
                <InputLabel shrink sx={{ color: '#fff' }}>{field.label}</InputLabel>
                <Select
                  value={fields[field.name] || false}
                  onChange={e => setFields({ ...fields, [field.name]: e.target.value === 'true' })}
                  displayEmpty
                  sx={{ color: '#fff', background: '#23243a', '.MuiSvgIcon-root': { color: '#fff' }, '.MuiSelect-icon': { color: '#fff' } }}
                >
                  <MenuItem value={false} sx={{ color: '#fff', background: '#23243a' }}>No</MenuItem>
                  <MenuItem value={true} sx={{ color: '#fff', background: '#23243a' }}>Sí</MenuItem>
                </Select>
              </FormControl>
            ) : (
              <TextField
                label={field.label}
                name={field.name}
                type={field.type}
                value={fields[field.name] || ''}
                onChange={handleFieldChange}
                fullWidth
                InputLabelProps={{ shrink: true, style: { color: '#fff' } }}
                sx={{ input: { color: '#fff', background: '#23243a' }, label: { color: '#fff' } }}
              />
            )}
          </Box>
        ))}
        <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
          Guardar Puntuación
        </Button>
      </form>
      <Snackbar open={snackbar.open} autoHideDuration={3000} onClose={() => setSnackbar({ ...snackbar, open: false })}>
        <Alert onClose={() => setSnackbar({ ...snackbar, open: false })} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
      <Button sx={{ mt: 2, color: '#fff' }} onClick={() => navigate('/leagues')}>Volver a Ligas</Button>
    </Box>
  );
}

// 3. Esqueleto de formulario para posiciones esperadas
export function ExpectedPositionsForm({ pilots, onSubmit }) {
  const [positions, setPositions] = useState(Array(20).fill(''));
  const [selectedPilots, setSelectedPilots] = useState(Array(20).fill(''));
  const availablePilots = pilots.filter(p => !selectedPilots.includes(p.id));

  const handlePilotChange = (idx, value) => {
    const newSelected = [...selectedPilots];
    newSelected[idx] = value;
    setSelectedPilots(newSelected);
  };
  const handlePositionChange = (idx, value) => {
    const newPositions = [...positions];
    newPositions[idx] = value;
    setPositions(newPositions);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(selectedPilots, positions);
  };
  return (
    <Box sx={{ maxWidth: 600, mx: 'auto', mt: 4, p: 3, background: '#181818', borderRadius: 3 }}>
      <Typography variant="h6" sx={{ color: '#fff', mb: 2 }}>Asignar Posiciones Esperadas</Typography>
      <form onSubmit={handleSubmit}>
        {[...Array(20)].map((_, idx) => (
          <Box key={idx} sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
            <FormControl sx={{ flex: 2, mr: 2 }}>
              <InputLabel sx={{ color: '#fff' }}>Piloto</InputLabel>
              <Select
                value={selectedPilots[idx]}
                onChange={e => handlePilotChange(idx, e.target.value)}
                sx={selectSx}
                required
              >
                {pilots.filter(p => !selectedPilots.includes(p.id) || selectedPilots[idx] === p.id).map(p => (
                  <MenuItem key={p.id} value={p.id} sx={menuItemSx}>{p.driver_name}</MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              label={`Posición #${idx + 1}`}
              type="number"
              value={positions[idx]}
              onChange={e => handlePositionChange(idx, e.target.value)}
              sx={{ input: { color: '#fff', background: '#23243a' }, label: { color: '#fff' }, flex: 1 }}
              required
            />
          </Box>
        ))}
        <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
          Guardar Posiciones Esperadas
        </Button>
      </form>
    </Box>
  );
}
