import React, { useState, useEffect } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import Avatar from '@mui/material/Avatar';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import Button from '@mui/material/Button';
import Divider from '@mui/material/Divider';

export default function MarketPage() {
  const [pilotos, setPilotos] = useState([]);
  const [open, setOpen] = useState(false);
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    fetch('/api/drivers/random')
      .then(res => res.json())
      .then(data => setPilotos(data.drivers || []));
  }, []);

  const handleClick = (piloto) => {
    setSelected(piloto);
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
    setSelected(null);
  };

  return (
    <Box sx={{ p: 2 }}>
      <Typography variant="h5" gutterBottom>Mercado de Pilotos</Typography>
      {pilotos.map((piloto) => (
        <Paper key={piloto.id} sx={{ display: 'flex', alignItems: 'center', p: 2, mb: 2, cursor: 'pointer' }} onClick={() => handleClick(piloto)}>
          <Avatar src={`/images/${piloto.image_url}`} alt={piloto.driver_name} sx={{ width: 56, height: 56, mr: 2 }} />
          <Box sx={{ flexGrow: 1 }}>
            <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>{piloto.driver_name}</Typography>
            <Typography variant="body2" color="text.secondary">{piloto.team}</Typography>
          </Box>
          <Typography variant="body1" sx={{ fontWeight: 700, color: '#fbc02d' }}>{piloto.valor ? piloto.valor + ' €' : ''}</Typography>
        </Paper>
      ))}

      {/* Dialog de perfil de piloto */}
      <Dialog open={open} onClose={handleClose} maxWidth="xs" fullWidth>
        {selected && (
          <>
            <DialogTitle sx={{ textAlign: 'center' }}>{selected.driver_name}</DialogTitle>
            <DialogContent>
              <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', mb: 2 }}>
                <Avatar src={`/images/${selected.image_url}`} alt={selected.driver_name} sx={{ width: 100, height: 100, mb: 1 }} />
                <Typography variant="subtitle1" color="text.secondary">{selected.team}</Typography>
                <Typography variant="h6" sx={{ color: '#fbc02d', fontWeight: 700 }}>{selected.valor ? selected.valor + ' €' : ''}</Typography>
              </Box>
              <Divider sx={{ mb: 2 }} />
              {/* Aquí puedes mostrar el historial de valor si lo tienes en el backend */}
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose} variant="contained" color="primary" fullWidth>Cerrar</Button>
            </DialogActions>
          </>
        )}
      </Dialog>
    </Box>
  );
}
