import React, { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import Alert from '@mui/material/Alert';
import { useLeague } from '../context/LeagueContext';
import DriverRaceCard from '../components/DriverRaceCard';
import { useNavigate } from 'react-router-dom';

export default function TeamPilotsPage() {
  const { selectedLeague } = useLeague();
  const [drivers, setDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDrivers = async () => {
      setLoading(true);
      try {
        const player_id = localStorage.getItem('player_id');
        
        if (!player_id) {
          setError('Debes iniciar sesiÃ³n');
          setLoading(false);
          return;
        }

        if (!selectedLeague) {
          setError('Debes seleccionar una liga');
          setLoading(false);
          return;
        }

        const res = await fetch(`/api/players/${player_id}/drivers?league_id=${selectedLeague.id}`);
        const data = await res.json();
        
        if (!res.ok) {
          throw new Error(data.error || 'Error cargando pilotos');
        }
        
        setDrivers(data.drivers || []);
      } catch (err) {
        setError('Error cargando pilotos: ' + err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchDrivers();
  }, [selectedLeague]);

  if (loading) return (
    <Box sx={{ 
      p: 2, 
      textAlign: 'center', 
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%)',
      minHeight: '100vh'
    }}>
      <Typography sx={{ color: '#fff', fontSize: '1.2rem' }}>
        ð Cargando tu equipo...
      </Typography>
    </Box>
  );

  if (error) return (
    <Box sx={{ 
      p: 2, 
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%)',
      minHeight: '100vh'
    }}>
      <Alert severity="error" sx={{ m: 2 }}>{error}</Alert>
    </Box>
  );

  if (!selectedLeague) {
    return (
      <Box sx={{ 
        p: 2, 
        textAlign: 'center', 
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%)',
        minHeight: '100vh'
      }}>
        <Typography variant="h6" sx={{ color: '#fff' }}>
          ðï¸ Por favor selecciona una liga para ver tus pilotos
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ 
      p: 2, 
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%)', 
      minHeight: '100vh' 
    }}>
      <Box sx={{ textAlign: 'center', mb: 4 }}>
        <Typography 
          variant="h4" 
          sx={{ 
            fontWeight: 700, 
            color: '#fff',
            textShadow: '0 2px 4px rgba(0,0,0,0.5)',
            background: 'linear-gradient(45deg, #DC0000, #FF4444)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            mb: 1
          }}
        >
          ð Mi Equipo
        </Typography>
        <Typography 
          variant="h6" 
          sx={{ 
            color: '#b0b0b0',
            fontWeight: 500
          }}
        >
          {selectedLeague.name}
        </Typography>
      </Box>

      {drivers.length === 0 ? (
        <Box sx={{ textAlign: 'center', py: 6 }}>
          <Typography 
            variant="h6" 
            sx={{ 
              color: '#fff', 
              mb: 2,
              fontSize: '1.3rem'
            }}
          >
            ð No tienes pilotos en esta liga
          </Typography>
          <Typography 
            variant="body1" 
            sx={{ 
              color: '#b0b0b0',
              maxWidth: 400,
              mx: 'auto'
            }}
          >
            Ve al mercado para adquirir pilotos y formar tu equipo de F1 Fantasy
          </Typography>
        </Box>
      ) : (
        <Box>
          <Box sx={{ textAlign: 'center', mb: 3 }}>
            <Typography 
              variant="h5" 
              sx={{ 
                color: '#fff', 
                fontWeight: 700,
                textShadow: '0 2px 4px rgba(0,0,0,0.5)'
              }}
            >
              ðï¸ {drivers.length} Piloto{drivers.length !== 1 ? 's' : ''} en tu Equipo
            </Typography>
          </Box>
          
          <Grid container spacing={3}>
            {drivers.map((driver) => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={driver.id}>
                <DriverRaceCard
                  driver={driver}
                  showStats={true}
                  isOwned={true}
                  onClick={() => {
                    navigate(`/profile/${driver.id}?league_id=${selectedLeague.id}`);
                  }}
                />
              </Grid>
            ))}
          </Grid>

          {/* EstadÃ­sticas del equipo */}
          <Box sx={{ 
            mt: 4, 
            p: 3, 
            background: 'linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%)',
            borderRadius: 3,
            border: '2px solid #DC0000'
          }}>
            <Typography 
              variant="h6" 
              sx={{ 
                color: '#fff', 
                fontWeight: 700, 
                mb: 2,
                textAlign: 'center'
              }}
            >
              ð EstadÃ­sticas del Equipo
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={4}>
                <Box sx={{ textAlign: 'center' }}>
                  <Typography variant="h4" sx={{ color: '#4CAF50', fontWeight: 700 }}>
                    {drivers.reduce((sum, driver) => sum + (driver.valor_global || driver.valorGlobal || 0), 0).toLocaleString()} â¬
                  </Typography>
                  <Typography variant="body2" sx={{ color: '#b0b0b0' }}>
                    Valor Total
                  </Typography>
                </Box>
              </Grid>
              <Grid item xs={12} sm={4}>
                <Box sx={{ textAlign: 'center' }}>
                  <Typography variant="h4" sx={{ color: '#FF9800', fontWeight: 700 }}>
                    {drivers.reduce((sum, driver) => sum + (driver.total_points || 0), 0)}
                  </Typography>
                  <Typography variant="body2" sx={{ color: '#b0b0b0' }}>
                    Puntos Totales
                  </Typography>
                </Box>
              </Grid>
              <Grid item xs={12} sm={4}>
                <Box sx={{ textAlign: 'center' }}>
                  <Typography variant="h4" sx={{ color: '#2196F3', fontWeight: 700 }}>
                    {drivers.reduce((sum, driver) => sum + (driver.week_points || 0), 0)}
                  </Typography>
                  <Typography variant="body2" sx={{ color: '#b0b0b0' }}>
                    Puntos Semana
                  </Typography>
                </Box>
              </Grid>
            </Grid>
          </Box>
        </Box>
      )}
    </Box>
  );
}
