import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Alert from '@mui/material/Alert';

export default function AuctionPilotBidPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [auction, setAuction] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [amount, setAmount] = useState('');
  const [msg, setMsg] = useState('');
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const fetchAuction = async () => {
      setLoading(true);
      try {
        const res = await fetch('/api/auctions');
        const data = await res.json();
        const found = (data.auctions || []).find(a => String(a.id) === String(id));
        setAuction(found);
      } catch (err) {
        setError('Error cargando la subasta');
      } finally {
        setLoading(false);
      }
    };
    fetchAuction();
  }, [id]);

  const handleBid = async () => {
    setMsg('');
    setError('');
    if (!amount || isNaN(amount) || Number(amount) <= auction.puja_actual) {
      setError('Introduce una cantidad válida mayor a la puja actual');
      return;
    }
    const player_id = localStorage.getItem('player_id');
    if (!player_id) {
      setError('Debes iniciar sesión para pujar');
      return;
    }
    setSubmitting(true);
    try {
      const res = await fetch('/api/auctions/bid', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          auction_driver_id: auction.id,
          player_id: Number(player_id),
          amount: Number(amount)
        })
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Error al pujar');
      setMsg('¡Puja realizada!');
      setTimeout(() => navigate(`/market/auction/${auction.id}`), 1500);
    } catch (err) {
      setError(err.message);
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return <Typography sx={{ p: 2 }}>Cargando...</Typography>;
  if (error) return <Alert severity="error">{error}</Alert>;
  if (!auction) return <Alert severity="warning">Piloto no encontrado en subasta.</Alert>;

  const { driver } = auction;

  return (
    <Box sx={{ maxWidth: 500, mx: 'auto', mt: 4, p: 3, background: '#181c24', borderRadius: 3, color: '#fff', boxShadow: 3 }}>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
        <Avatar src={driver?.image_url ? `/images/${driver.image_url}` : ''} alt={driver?.driver_name} sx={{ width: 70, height: 70, mr: 3, border: '2px solid #fff' }} />
        <Box>
          <Typography variant="h6" sx={{ fontWeight: 700 }}>{driver?.driver_name}</Typography>
          <Typography variant="subtitle2" color="#b0b0b0" sx={{ fontWeight: 500 }}>{driver?.team}</Typography>
        </Box>
      </Box>
      <Typography variant="body2" color="#b0b0b0">Puja actual: <span style={{ color: '#f44336', fontWeight: 700 }}>{auction.puja_actual.toLocaleString()} €</span></Typography>
      <TextField
        label="Tu puja (€)"
        type="number"
        value={amount}
        onChange={e => setAmount(e.target.value)}
        fullWidth
        sx={{ mt: 2, background: '#fff', borderRadius: 1 }}
        InputProps={{ inputProps: { min: auction.puja_actual + 1 } }}
      />
      <Button
        variant="contained"
        color="error"
        sx={{ mt: 2, fontWeight: 700, fontSize: 16, px: 4, py: 1.5 }}
        fullWidth
        disabled={submitting}
        onClick={handleBid}
      >
        PUJAR
      </Button>
      {msg && <Alert severity="success" sx={{ mt: 2 }}>{msg}</Alert>}
      {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
      <Button variant="text" sx={{ mt: 2, color: '#90caf9' }} onClick={() => navigate(`/market/auction/${auction.id}`)}>
        Volver al perfil del piloto
      </Button>
    </Box>
  );
}
