import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import CircularProgress from '@mui/material/CircularProgress';
import { useLeague } from '../context/LeagueContext';

// Colores de equipos de F1
const teamColors = {
  'Red Bull Racing': { primary: '#3671C6', secondary: '#1E41C3' },
  'Mercedes': { primary: '#6CD3BF', secondary: '#00D2BE' },
  'McLaren': { primary: '#FF8700', secondary: '#FF5800' },
  'Ferrari': { primary: '#DC0000', secondary: '#B80000' },
  'Aston Martin': { primary: '#358C75', secondary: '#006F62' },
  'Alpine': { primary: '#0090FF', secondary: '#0051FF' },
  'Stake F1 Team Kick Sauber': { primary: '#52E252', secondary: '#37BEDD' },
  'Haas': { primary: '#FFFFFF', secondary: '#E8E8E8' },
  'Williams': { primary: '#37BEDD', secondary: '#005AFF' },
  'Visa Cash App RB': { primary: '#5E8FAA', secondary: '#1E41C3' }
};

export default function AuctionPilotBidPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { selectedLeague } = useLeague();
  const [auction, setAuction] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [amount, setAmount] = useState('');
  const [msg, setMsg] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [saldo, setSaldo] = useState(null);
  const [saldoLoading, setSaldoLoading] = useState(false);

  useEffect(() => {
    const fetchAuction = async () => {
      setLoading(true);
      try {
        const player_id = localStorage.getItem('player_id');
        let url = '/api/auctions';
        if (player_id && selectedLeague) {
          url = `/api/auctions?player_id=${player_id}&league_id=${selectedLeague.id}`;
        } else if (selectedLeague) {
          url = `/api/auctions?league_id=${selectedLeague.id}`;
        }
        const res = await fetch(url);
        const data = await res.json();
        const found = (data.auctions || []).find(a => String(a.id) === String(id));
        setAuction(found);
      } catch (err) {
        setError('Error cargando la subasta');
      } finally {
        setLoading(false);
      }
    };
    fetchAuction();
  }, [id, selectedLeague]);

  useEffect(() => {
    // Obtener saldo al abrir el modal
    const fetchSaldo = async () => {
      setSaldoLoading(true);
      try {
        const player_id = localStorage.getItem('player_id');
        if (player_id && selectedLeague) {
          const res = await fetch(`/api/playerbyleague?player_id=${player_id}&league_id=${selectedLeague.id}`);
          const data = await res.json();
          setSaldo(data.player_by_league?.money ?? 0);
        }
      } catch (e) {
        setSaldo(0);
      } finally {
        setSaldoLoading(false);
      }
    };
    fetchSaldo();
  }, [selectedLeague]);

  const handleBid = async () => {
    setMsg('');
    setError('');
    if (!amount || isNaN(amount) || Number(amount) <= auction.puja_actual) {
      setError('Introduce una cantidad v√°lida mayor a la puja actual');
      return;
    }
    const player_id = localStorage.getItem('player_id');
    if (!player_id) {
      setError('Debes iniciar sesi√≥n para pujar');
      return;
    }
    setSubmitting(true);
    try {
      const res = await fetch('/api/auctions/bid', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          auction_driver_id: auction.id,
          player_id: Number(player_id),
          amount: Number(amount)
        })
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Error al pujar');
      setMsg('¬°Puja realizada!');
      setTimeout(() => navigate(`/market/auction/${auction.id}`), 1500);
    } catch (err) {
      setError(err.message);
    } finally {
      setSubmitting(false);
    }
  };

  const handleClose = () => {
    navigate(`/market/auction/${id}`);
  };

  if (loading) return (
    <Box sx={{ 
      position: 'fixed', 
      top: 0, 
      left: 0, 
      right: 0, 
      bottom: 0, 
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%)', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      zIndex: 1000 
    }}>
      <Typography sx={{ color: '#fff', fontSize: '1.2rem' }}>üèÅ Cargando puja...</Typography>
    </Box>
  );

  if (error) return (
    <Box sx={{ 
      position: 'fixed', 
      top: 0, 
      left: 0, 
      right: 0, 
      bottom: 0, 
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%)', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      zIndex: 1000 
    }}>
      <Alert severity="error">{error}</Alert>
    </Box>
  );

  if (!auction) return (
    <Box sx={{ 
      position: 'fixed', 
      top: 0, 
      left: 0, 
      right: 0, 
      bottom: 0, 
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%)', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      zIndex: 1000 
    }}>
      <Alert severity="warning">üèÅ Piloto no encontrado en subasta.</Alert>
    </Box>
  );

  const { driver } = auction;
  const teamColor = teamColors[driver?.team] || { primary: '#666666', secondary: '#444444' };

  return (
    <Box 
      sx={{ 
        position: 'fixed', 
        top: 0, 
        left: 0, 
        right: 0, 
        bottom: 0, 
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%)', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        zIndex: 1000 
      }}
      onClick={handleClose}
    >
      <Box 
        sx={{ 
          maxWidth: 500, 
          width: '90%',
          p: 4, 
          background: 'linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%)', 
          borderRadius: 4, 
          color: '#fff', 
          boxShadow: `0 20px 40px rgba(0,0,0,0.8), 0 0 20px rgba(${teamColor.primary}, 0.3)`,
          position: 'relative',
          border: `2px solid ${teamColor.primary}`,
          overflow: 'hidden',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            height: '4px',
            background: `linear-gradient(90deg, ${teamColor.primary}, ${teamColor.secondary})`,
          }
        }}
        onClick={(e) => e.stopPropagation()}
      >
        <IconButton
          onClick={handleClose}
          sx={{
            position: 'absolute',
            top: 12,
            right: 12,
            color: '#fff',
            background: 'rgba(0,0,0,0.3)',
            '&:hover': {
              background: 'rgba(0,0,0,0.5)'
            }
          }}
        >
          <CloseIcon />
        </IconButton>
        
        {/* Header con foto y nombre mejorada */}
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
          <img 
            src={driver?.image_url ? `/images/${driver.image_url}` : ''} 
            alt={driver?.driver_name} 
            style={{
              width: 120,
              height: 120,
              objectFit: 'cover',
              borderRadius: '50%',
              boxShadow: `0 4px 16px ${teamColor.primary}99`,
              border: `4px solid ${teamColor.primary}`,
              marginRight: 24,
              background: '#fff',
              display: 'block',
            }}
          />
          <Box>
            <Typography 
              variant="h5" 
              sx={{ 
                fontWeight: 700,
                color: '#fff',
                textShadow: '0 2px 4px rgba(0,0,0,0.5)',
                mb: 1
              }}
            >
              {driver?.driver_name}
            </Typography>
            <Typography 
              variant="subtitle1" 
              sx={{ 
                color: teamColor.primary,
                fontWeight: 600,
                textTransform: 'uppercase',
                letterSpacing: '0.5px'
              }}
            >
              {driver?.team}
            </Typography>
          </Box>
        </Box>
        {/* Mostrar saldo */}
        <Box sx={{ mb: 2, display: 'flex', alignItems: 'center' }}>
          <Typography variant="body1" sx={{ color: '#b0b0b0', mr: 1 }}>
            Tu saldo en esta liga:
          </Typography>
          {saldoLoading ? (
            <CircularProgress size={18} sx={{ color: teamColor.primary }} />
          ) : (
            <Typography variant="h6" sx={{ color: saldo === 0 ? '#f44336' : '#4caf50', fontWeight: 700 }}>
              {saldo?.toLocaleString('es-ES', { style: 'currency', currency: 'EUR' }) ?? '0 ‚Ç¨'}
            </Typography>
          )}
        </Box>

        {/* Informaci√≥n de puja actual */}
        <Box sx={{ 
          mb: 3,
          p: 2,
          background: 'rgba(0,0,0,0.2)',
          borderRadius: 2,
          border: `1px solid ${teamColor.primary}`
        }}>
          <Typography variant="body1" sx={{ color: '#b0b0b0', mb: 1 }}>
            Puja actual:
          </Typography>
          <Typography variant="h4" sx={{ fontWeight: 700, color: '#f44336' }}>
            {auction.puja_visible.toLocaleString()} ‚Ç¨
          </Typography>
          {auction.mi_puja_maxima > 0 && (
            <Typography variant="body2" sx={{ color: '#90caf9', mt: 1 }}>
              Mi puja m√°xima: <span style={{ fontWeight: 700 }}>{auction.mi_puja_maxima.toLocaleString()} ‚Ç¨</span>
            </Typography>
          )}
        </Box>

        {/* Campo de puja */}
        <TextField
          label="Tu puja (‚Ç¨)"
          type="number"
          value={amount}
          onChange={e => setAmount(e.target.value)}
          fullWidth
          sx={{ 
            mb: 3,
            '& .MuiOutlinedInput-root': {
              background: '#fff',
              borderRadius: 2,
              '& fieldset': {
                borderColor: teamColor.primary,
                borderWidth: 2
              },
              '&:hover fieldset': {
                borderColor: teamColor.secondary
              },
              '&.Mui-focused fieldset': {
                borderColor: teamColor.secondary
              }
            },
            '& .MuiInputLabel-root': {
              color: '#b0b0b0'
            }
          }}
          InputProps={{ 
            inputProps: { 
              min: auction.puja_actual + 1,
              style: { fontSize: '1.1rem', fontWeight: 600 }
            } 
          }}
        />

        {/* Bot√≥n de puja */}
        <Button
          variant="contained"
          sx={{ 
            fontWeight: 700, 
            fontSize: 18, 
            px: 4, 
            py: 2, 
            width: '100%',
            background: `linear-gradient(45deg, ${teamColor.primary}, ${teamColor.secondary})`,
            '&:hover': {
              background: `linear-gradient(45deg, ${teamColor.secondary}, ${teamColor.primary})`,
              transform: 'translateY(-2px)',
              boxShadow: `0 8px 20px rgba(${teamColor.primary}, 0.4)`
            },
            '&:disabled': {
              background: '#666666',
              transform: 'none'
            }
          }}
          disabled={submitting}
          onClick={handleBid}
        >
          {submitting ? 'üèÅ PROCESANDO...' : 'üèÜ REALIZAR PUJA'}
        </Button>

        {/* Mensajes */}
        {msg && <Alert severity="success" sx={{ mt: 2 }}>{msg}</Alert>}
        {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}

        {/* L√≠nea decorativa inferior */}
        <Box
          sx={{
            position: 'absolute',
            bottom: 0,
            left: 0,
            right: 0,
            height: '3px',
            background: `linear-gradient(90deg, ${teamColor.primary}, ${teamColor.secondary})`,
            opacity: 0.8
          }}
        />
      </Box>
    </Box>
  );
}
