import React, { useState, useEffect } from 'react';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import { useLeague } from '../context/LeagueContext';

export default function ClasificationPage() {
  const { selectedLeague } = useLeague();
  const [classification, setClassification] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch classification when selected league changes
  useEffect(() => {
    if (selectedLeague) {
      fetchClassification();
    } else {
      setClassification([]);
    }
  }, [selectedLeague]);

  const fetchClassification = async () => {
    if (!selectedLeague) return;
    
    setLoading(true);
    setError('');
    const token = localStorage.getItem('token');
    if (!token) {
      setError('You must be logged in');
      setLoading(false);
      return;
    }

    try {
      const res = await fetch(`/api/leagues/${selectedLeague.id}/classification`, {
        headers: { 'Authorization': token }
      });
      if (!res.ok) throw new Error('Error fetching classification');
      const data = await res.json();
      setClassification(data.classification || []);
    } catch (err) {
      setError('Error loading classification');
    } finally {
      setLoading(false);
    }
  };

  if (!selectedLeague) {
    return (
      <Box sx={{ padding: 2, textAlign: 'center' }}>
        <Typography variant="h6" color="text.secondary">
          Please select a league to view the classification
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ padding: 2 }}>
      <Typography variant="h5" gutterBottom>
        Classification - {selectedLeague.name}
      </Typography>
      
      {error && (
        <Typography color="error" sx={{ mb: 2 }}>
          {error}
        </Typography>
      )}

      {loading ? (
        <Typography>Loading classification...</Typography>
      ) : (
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Position</TableCell>
                <TableCell>Player</TableCell>
                <TableCell align="right">Total Points</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {classification.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={3} align="center">
                    No players found in this league
                  </TableCell>
                </TableRow>
              ) : (
                classification.map((player, index) => (
                  <TableRow key={player.id}>
                    <TableCell>{index + 1}</TableCell>
                    <TableCell>{player.name}</TableCell>
                    <TableCell align="right">{player.total_points || 0}</TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </Box>
  );
}
