import React, { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import AuctionPilotCard from '../components/AuctionPilotCard';
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';

export default function MarketPage() {
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  const fetchAuctions = async () => {
    setLoading(true);
    try {
      const res = await fetch('/api/auctions');
      const data = await res.json();
      setAuctions(data.auctions || []);
    } catch (err) {
      setError('Error cargando el mercado');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAuctions();
    // Opcional: refrescar cada 10s para mantener tiempos actualizados
    const interval = setInterval(fetchAuctions, 10000);
    return () => clearInterval(interval);
  }, []);

  const handleBid = async (auction) => {
    // Aquí deberías obtener el player_id real del usuario logueado
    const player_id = localStorage.getItem('player_id');
    if (!player_id) {
      setSnackbar({ open: true, message: 'Debes iniciar sesión para pujar', severity: 'error' });
      return;
    }
    const amount = prompt('Introduce tu puja (mayor a la actual):', auction.puja_actual + 1);
    if (!amount || isNaN(amount) || Number(amount) <= auction.puja_actual) {
      setSnackbar({ open: true, message: 'Puja no válida', severity: 'error' });
      return;
    }
    try {
      const res = await fetch('/api/auctions/bid', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          auction_driver_id: auction.id,
          player_id: Number(player_id),
          amount: Number(amount)
        })
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Error al pujar');
      setSnackbar({ open: true, message: '¡Puja realizada!', severity: 'success' });
      fetchAuctions();
    } catch (err) {
      setSnackbar({ open: true, message: err.message, severity: 'error' });
    }
  };

  return (
    <Box sx={{ p: 2 }}>
      <Typography variant="h5" gutterBottom>Mercado de Pilotos (Subasta)</Typography>
      {loading && <Typography>Cargando...</Typography>}
      {error && <Alert severity="error">{error}</Alert>}
      {auctions.length === 0 && !loading && <Typography>No hay pilotos en subasta.</Typography>}
      {auctions.map(auction => (
        <AuctionPilotCard
          key={auction.id}
          photo={auction.driver?.image_url ? `/images/${auction.driver.image_url}` : ''}
          name={auction.driver?.driver_name || ''}
          team={auction.driver?.team || ''}
          valorBase={auction.valor_base}
          pujaActual={auction.puja_actual}
          tiempoRestante={Math.max(0, Math.floor((new Date(auction.fin_subasta).getTime() - Date.now()) / 1000))}
          numBids={auction.num_bids}
          onBid={() => handleBid(auction)}
          disabled={Math.floor((new Date(auction.fin_subasta).getTime() - Date.now()) / 1000) <= 0}
        />
      ))}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert onClose={() => setSnackbar({ ...snackbar, open: false })} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
}
