import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import GavelIcon from '@mui/icons-material/Gavel';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';

export default function AuctionPilotProfilePage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [auction, setAuction] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [seconds, setSeconds] = useState(0);

  useEffect(() => {
    const fetchAuction = async () => {
      setLoading(true);
      try {
        const player_id = localStorage.getItem('player_id');
        const url = player_id ? `/api/auctions?player_id=${player_id}` : '/api/auctions';
        const res = await fetch(url);
        const data = await res.json();
        const found = (data.auctions || []).find(a => String(a.id) === String(id));
        setAuction(found);
        if (found) {
          const t = Math.max(0, Math.floor((new Date(found.fin_subasta).getTime() - Date.now()) / 1000));
          setSeconds(t);
        }
      } catch (err) {
        setError('Error cargando la subasta');
      } finally {
        setLoading(false);
      }
    };
    fetchAuction();
    // Opcional: refrescar cada 10s
    const interval = setInterval(fetchAuction, 10000);
    return () => clearInterval(interval);
  }, [id]);

  useEffect(() => {
    if (seconds <= 0) return;
    const interval = setInterval(() => {
      setSeconds(s => (s > 0 ? s - 1 : 0));
    }, 1000);
    return () => clearInterval(interval);
  }, [seconds]);

  const formatTime = s => {
    const h = Math.floor(s / 3600);
    const m = Math.floor((s % 3600) / 60);
    const sec = s % 60;
    return `${h.toString().padStart(2, '0')}:${m.toString().padStart(2, '0')}:${sec.toString().padStart(2, '0')}`;
  };

  const handleClose = () => {
    navigate('/market');
  };

  if (loading) return (
    <Box sx={{ 
      position: 'fixed', 
      top: 0, 
      left: 0, 
      right: 0, 
      bottom: 0, 
      bgcolor: 'rgba(0,0,0,0.8)', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      zIndex: 1000 
    }}>
      <Typography sx={{ color: '#fff' }}>Cargando...</Typography>
    </Box>
  );

  if (error) return (
    <Box sx={{ 
      position: 'fixed', 
      top: 0, 
      left: 0, 
      right: 0, 
      bottom: 0, 
      bgcolor: 'rgba(0,0,0,0.8)', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      zIndex: 1000 
    }}>
      <Alert severity="error">{error}</Alert>
    </Box>
  );

  if (!auction) return (
    <Box sx={{ 
      position: 'fixed', 
      top: 0, 
      left: 0, 
      right: 0, 
      bottom: 0, 
      bgcolor: 'rgba(0,0,0,0.8)', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      zIndex: 1000 
    }}>
      <Alert severity="warning">Piloto no encontrado en subasta.</Alert>
    </Box>
  );

  const { driver } = auction;

  return (
    <Box 
      sx={{ 
        position: 'fixed', 
        top: 0, 
        left: 0, 
        right: 0, 
        bottom: 0, 
        bgcolor: 'rgba(0,0,0,0.8)', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        zIndex: 1000 
      }}
      onClick={handleClose}
    >
      <Box 
        sx={{ 
          maxWidth: 500, 
          width: '90%',
          p: 3, 
          background: '#181c24', 
          borderRadius: 3, 
          color: '#fff', 
          boxShadow: 3,
          position: 'relative'
        }}
        onClick={(e) => e.stopPropagation()}
      >
        <IconButton
          onClick={handleClose}
          sx={{
            position: 'absolute',
            top: 8,
            right: 8,
            color: '#fff'
          }}
        >
          <CloseIcon />
        </IconButton>
        
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Avatar src={driver?.image_url ? `/images/${driver.image_url}` : ''} alt={driver?.driver_name} sx={{ width: 90, height: 90, mr: 3, border: '2px solid #fff' }} />
          <Box>
            <Typography variant="h5" sx={{ fontWeight: 700 }}>{driver?.driver_name}</Typography>
            <Typography variant="subtitle1" color="#b0b0b0" sx={{ fontWeight: 500 }}>{driver?.team}</Typography>
          </Box>
        </Box>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <AccessTimeIcon sx={{ fontSize: 22, mr: 1, color: '#fbc02d' }} />
          <Typography variant="body1" sx={{ fontWeight: 600, color: '#fbc02d', mr: 3 }}>{formatTime(seconds)}</Typography>
          <GavelIcon sx={{ fontSize: 22, mr: 1, color: '#90caf9' }} />
          <Typography variant="body1" sx={{ fontWeight: 600, color: '#90caf9' }}>{auction.num_bids} pujas</Typography>
        </Box>
        <Box sx={{ display: 'flex', gap: 4, mb: 2 }}>
          <Box>
            <Typography variant="body2" color="#b0b0b0">Valor</Typography>
            <Typography variant="h6" sx={{ fontWeight: 700, color: '#fff' }}>{auction.valor_base.toLocaleString()} €</Typography>
          </Box>
          <Box>
            <Typography variant="body2" color="#b0b0b0">
              {auction.mi_puja_maxima > 0 ? 'Mi puja máxima' : 'Puja actual'}
            </Typography>
            <Typography variant="h6" sx={{ fontWeight: 700, color: '#f44336' }}>
              {auction.mi_puja_maxima > 0 ? auction.mi_puja_maxima.toLocaleString() : auction.puja_visible.toLocaleString()} €
            </Typography>
          </Box>
        </Box>
        <Button
          variant="contained"
          color="error"
          sx={{ fontWeight: 700, fontSize: 18, px: 4, py: 1.5, mt: 2 }}
          disabled={seconds === 0}
          onClick={() => navigate(`/market/auction/${auction.id}/bid`)}
        >
          PUJAR
        </Button>
        <Button
          variant="outlined"
          color="warning"
          sx={{ fontWeight: 700, fontSize: 14, px: 3, py: 1, mt: 1, ml: 1 }}
          onClick={async () => {
            try {
              const res = await fetch(`/api/auctions/${auction.id}/finish`, { method: 'POST' });
              if (res.ok) {
                navigate('/market');
              }
            } catch (err) {
              console.error('Error finalizando subasta:', err);
            }
          }}
        >
          FINALIZAR SUBASTA (PRUEBA)
        </Button>
      </Box>
    </Box>
  );
}
