import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import { useLeague } from '../context/LeagueContext';

export default function AuctionPilotBidPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { selectedLeague } = useLeague();
  const [auction, setAuction] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [amount, setAmount] = useState('');
  const [msg, setMsg] = useState('');
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const fetchAuction = async () => {
      setLoading(true);
      try {
        const player_id = localStorage.getItem('player_id');
        let url = '/api/auctions';
        if (player_id && selectedLeague) {
          url = `/api/auctions?player_id=${player_id}&league_id=${selectedLeague.id}`;
        } else if (selectedLeague) {
          url = `/api/auctions?league_id=${selectedLeague.id}`;
        }
        const res = await fetch(url);
        const data = await res.json();
        const found = (data.auctions || []).find(a => String(a.id) === String(id));
        setAuction(found);
      } catch (err) {
        setError('Error cargando la subasta');
      } finally {
        setLoading(false);
      }
    };
    fetchAuction();
  }, [id, selectedLeague]);

  const handleBid = async () => {
    setMsg('');
    setError('');
    if (!amount || isNaN(amount) || Number(amount) <= auction.puja_actual) {
      setError('Introduce una cantidad válida mayor a la puja actual');
      return;
    }
    const player_id = localStorage.getItem('player_id');
    if (!player_id) {
      setError('Debes iniciar sesión para pujar');
      return;
    }
    setSubmitting(true);
    try {
      const res = await fetch('/api/auctions/bid', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          auction_driver_id: auction.id,
          player_id: Number(player_id),
          amount: Number(amount)
        })
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Error al pujar');
      setMsg('¡Puja realizada!');
      setTimeout(() => navigate(`/market/auction/${auction.id}`), 1500);
    } catch (err) {
      setError(err.message);
    } finally {
      setSubmitting(false);
    }
  };

  const handleClose = () => {
    navigate(`/market/auction/${id}`);
  };

  if (loading) return (
    <Box sx={{ 
      position: 'fixed', 
      top: 0, 
      left: 0, 
      right: 0, 
      bottom: 0, 
      bgcolor: 'rgba(0,0,0,0.8)', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      zIndex: 1000 
    }}>
      <Typography sx={{ color: '#fff' }}>Cargando...</Typography>
    </Box>
  );

  if (error) return (
    <Box sx={{ 
      position: 'fixed', 
      top: 0, 
      left: 0, 
      right: 0, 
      bottom: 0, 
      bgcolor: 'rgba(0,0,0,0.8)', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      zIndex: 1000 
    }}>
      <Alert severity="error">{error}</Alert>
    </Box>
  );

  if (!auction) return (
    <Box sx={{ 
      position: 'fixed', 
      top: 0, 
      left: 0, 
      right: 0, 
      bottom: 0, 
      bgcolor: 'rgba(0,0,0,0.8)', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      zIndex: 1000 
    }}>
      <Alert severity="warning">Piloto no encontrado en subasta.</Alert>
    </Box>
  );

  const { driver } = auction;

  return (
    <Box 
      sx={{ 
        position: 'fixed', 
        top: 0, 
        left: 0, 
        right: 0, 
        bottom: 0, 
        bgcolor: 'rgba(0,0,0,0.8)', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        zIndex: 1000 
      }}
      onClick={handleClose}
    >
      <Box 
        sx={{ 
          maxWidth: 500, 
          width: '90%',
          p: 3, 
          background: '#181c24', 
          borderRadius: 3, 
          color: '#fff', 
          boxShadow: 3,
          position: 'relative'
        }}
        onClick={(e) => e.stopPropagation()}
      >
        <IconButton
          onClick={handleClose}
          sx={{
            position: 'absolute',
            top: 8,
            right: 8,
            color: '#fff'
          }}
        >
          <CloseIcon />
        </IconButton>
        
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Avatar src={driver?.image_url ? `/images/${driver.image_url}` : ''} alt={driver?.driver_name} sx={{ width: 70, height: 70, mr: 3, border: '2px solid #fff' }} />
          <Box>
            <Typography variant="h6" sx={{ fontWeight: 700 }}>{driver?.driver_name}</Typography>
            <Typography variant="subtitle2" color="#b0b0b0" sx={{ fontWeight: 500 }}>{driver?.team}</Typography>
          </Box>
        </Box>
        <Typography variant="body2" color="#b0b0b0">
          Puja actual: <span style={{ color: '#f44336', fontWeight: 700 }}>{auction.puja_visible.toLocaleString()} €</span>
        </Typography>
        {auction.mi_puja_maxima > 0 && (
          <Typography variant="body2" color="#90caf9" sx={{ mt: 1 }}>
            Mi puja máxima: <span style={{ fontWeight: 700 }}>{auction.mi_puja_maxima.toLocaleString()} €</span>
          </Typography>
        )}
        <TextField
          label="Tu puja (€)"
          type="number"
          value={amount}
          onChange={e => setAmount(e.target.value)}
          fullWidth
          sx={{ mt: 2, background: '#fff', borderRadius: 1 }}
          InputProps={{ inputProps: { min: auction.puja_actual + 1 } }}
        />
        <Button
          variant="contained"
          color="error"
          sx={{ mt: 2, fontWeight: 700, fontSize: 16, px: 4, py: 1.5 }}
          fullWidth
          disabled={submitting}
          onClick={handleBid}
        >
          PUJAR
        </Button>
        {msg && <Alert severity="success" sx={{ mt: 2 }}>{msg}</Alert>}
        {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
      </Box>
    </Box>
  );
}
