import React, { useEffect, useState } from 'react';
import { Box, Typography, Button, MenuItem, Select, TextField, FormControl, InputLabel, Snackbar, Alert } from '@mui/material';
import { useNavigate } from 'react-router-dom';

export default function AdminScoresPage() {
  const [pilots, setPilots] = useState([]);
  const [gps, setGps] = useState([]);
  const [sessionType, setSessionType] = useState('race');
  const [pilotId, setPilotId] = useState('');
  const [gpIndex, setGpIndex] = useState('');
  const [fields, setFields] = useState({});
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const navigate = useNavigate();

  useEffect(() => {
    // Cargar pilotos y GPs
    fetch('/api/pilots').then(res => res.json()).then(data => setPilots(data.pilots || []));
    fetch('/api/grand-prix').then(res => res.json()).then(data => setGps(data.gps || []));
  }, []);

  const handleFieldChange = (e) => {
    setFields({ ...fields, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let endpoint = '';
    if (sessionType === 'race') endpoint = '/api/admin/pilot-race';
    if (sessionType === 'qualy') endpoint = '/api/admin/pilot-qualy';
    if (sessionType === 'practice') endpoint = '/api/admin/pilot-practice';
    const res = await fetch(endpoint, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ pilot_id: pilotId, gp_index: gpIndex, ...fields })
    });
    const data = await res.json();
    if (res.ok) {
      setSnackbar({ open: true, message: 'Puntuación guardada', severity: 'success' });
    } else {
      setSnackbar({ open: true, message: data.error || 'Error al guardar', severity: 'error' });
    }
  };

  // Campos por tipo de sesión
  const sessionFields = {
    race: [
      { name: 'start_position', label: 'Posición de salida', type: 'number' },
      { name: 'finish_position', label: 'Posición final', type: 'number' },
      { name: 'expected_position', label: 'Posición esperada', type: 'number' },
      { name: 'delta_position', label: 'Delta posición', type: 'number' },
      { name: 'points', label: 'Puntos', type: 'number' },
      { name: 'positions_gained_at_start', label: 'Posiciones ganadas en salida', type: 'number' },
      { name: 'clean_overtakes', label: 'Adelantamientos limpios', type: 'number' },
      { name: 'net_positions_lost', label: 'Posiciones netas perdidas', type: 'number' },
      { name: 'fastest_lap', label: 'Vuelta rápida', type: 'checkbox' },
      { name: 'caused_vsc', label: 'Causó VSC', type: 'checkbox' },
      { name: 'caused_sc', label: 'Causó SC', type: 'checkbox' },
      { name: 'caused_red_flag', label: 'Causó bandera roja', type: 'checkbox' },
      { name: 'dnf_driver_error', label: 'DNF error piloto', type: 'checkbox' },
      { name: 'dnf_no_fault', label: 'DNF sin culpa', type: 'checkbox' },
    ],
    qualy: [
      { name: 'start_position', label: 'Posición de salida', type: 'number' },
      { name: 'finish_position', label: 'Posición final', type: 'number' },
      { name: 'expected_position', label: 'Posición esperada', type: 'number' },
      { name: 'delta_position', label: 'Delta posición', type: 'number' },
      { name: 'points', label: 'Puntos', type: 'number' },
      { name: 'caused_red_flag', label: 'Causó bandera roja', type: 'checkbox' },
    ],
    practice: [
      { name: 'start_position', label: 'Posición de salida', type: 'number' },
      { name: 'finish_position', label: 'Posición final', type: 'number' },
      { name: 'expected_position', label: 'Posición esperada', type: 'number' },
      { name: 'delta_position', label: 'Delta posición', type: 'number' },
      { name: 'points', label: 'Puntos', type: 'number' },
      { name: 'caused_red_flag', label: 'Causó bandera roja', type: 'checkbox' },
    ]
  };

  return (
    <Box sx={{ maxWidth: 500, mx: 'auto', mt: 4, p: 3, background: '#181818', borderRadius: 3 }}>
      <Typography variant="h5" sx={{ mb: 2, color: '#fff' }}>Administrar Puntuaciones Manuales</Typography>
      <form onSubmit={handleSubmit}>
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Piloto</InputLabel>
          <Select value={pilotId} label="Piloto" onChange={e => setPilotId(e.target.value)} required>
            {pilots.map(p => <MenuItem key={p.id} value={p.id}>{p.driver_name}</MenuItem>)}
          </Select>
        </FormControl>
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Gran Premio</InputLabel>
          <Select value={gpIndex} label="Gran Premio" onChange={e => setGpIndex(e.target.value)} required>
            {gps.map(gp => <MenuItem key={gp.gp_index} value={gp.gp_index}>{gp.name}</MenuItem>)}
          </Select>
        </FormControl>
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Sesión</InputLabel>
          <Select value={sessionType} label="Sesión" onChange={e => setSessionType(e.target.value)} required>
            <MenuItem value="race">Carrera</MenuItem>
            <MenuItem value="qualy">Clasificación</MenuItem>
            <MenuItem value="practice">Práctica</MenuItem>
          </Select>
        </FormControl>
        {sessionFields[sessionType].map(field => (
          <Box key={field.name} sx={{ mb: 2 }}>
            {field.type === 'checkbox' ? (
              <FormControl fullWidth>
                <InputLabel shrink>{field.label}</InputLabel>
                <Select
                  value={fields[field.name] || false}
                  onChange={e => setFields({ ...fields, [field.name]: e.target.value === 'true' })}
                  displayEmpty
                >
                  <MenuItem value={false}>No</MenuItem>
                  <MenuItem value={true}>Sí</MenuItem>
                </Select>
              </FormControl>
            ) : (
              <TextField
                label={field.label}
                name={field.name}
                type={field.type}
                value={fields[field.name] || ''}
                onChange={handleFieldChange}
                fullWidth
                InputLabelProps={{ shrink: true }}
              />
            )}
          </Box>
        ))}
        <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
          Guardar Puntuación
        </Button>
      </form>
      <Snackbar open={snackbar.open} autoHideDuration={3000} onClose={() => setSnackbar({ ...snackbar, open: false })}>
        <Alert onClose={() => setSnackbar({ ...snackbar, open: false })} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
      <Button sx={{ mt: 2 }} onClick={() => navigate('/leagues')}>Volver a Ligas</Button>
    </Box>
  );
}
