import React, { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';
import { useNavigate } from 'react-router-dom';
import Button from '@mui/material/Button';
import { useLeague } from '../context/LeagueContext';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import Grid from '@mui/material/Grid';
import DriverRaceCard from '../components/DriverRaceCard';

export default function MarketPage() {
  const { selectedLeague } = useLeague();
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const [openDrivers, setOpenDrivers] = useState(false);
  const [drivers, setDrivers] = useState([]);
  const [loadingDrivers, setLoadingDrivers] = useState(false);
  const [driversError, setDriversError] = useState('');
  const navigate = useNavigate();

  const fetchDrivers = async () => {
    if (!selectedLeague) return;
    setLoadingDrivers(true);
    setDriversError('');
    try {
      const res = await fetch(`/api/drivers?league_id=${selectedLeague.id}`);
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Error cargando pilotos');
      setDrivers(data.drivers || []);
    } catch (err) {
      setDriversError('Error cargando pilotos: ' + err.message);
    } finally {
      setLoadingDrivers(false);
    }
  };

  const fetchAuctions = async () => {
    setLoading(true);
    try {
      const player_id = localStorage.getItem('player_id');
      if (!selectedLeague) {
        setError('Debes seleccionar una liga');
        setLoading(false);
        return;
      }
      const url = `/api/auctions?player_id=${player_id}&league_id=${selectedLeague.id}`;
      const res = await fetch(url);
      const data = await res.json();
      setAuctions(data.auctions || []);
    } catch (err) {
      setError('Error cargando el mercado');
    } finally {
      setLoading(false);
    }
  };

  const handleFinishAllAuctions = async () => {
    try {
      if (!selectedLeague) return;
      const res = await fetch(`/api/auctions/finish-all?league_id=${selectedLeague.id}`, { method: 'POST' });
      if (res.ok) {
        setSnackbar({ open: true, message: 'Todas las subastas han sido finalizadas', severity: 'success' });
        fetchAuctions(); // Recargar la lista
      } else {
        const data = await res.json();
        setSnackbar({ open: true, message: data.error || 'Error finalizando subastas', severity: 'error' });
      }
    } catch (err) {
      setSnackbar({ open: true, message: 'Error finalizando subastas', severity: 'error' });
    }
  };

  const handleDriverClick = (driver) => {
    // Buscar si el piloto estÃ¡ en subasta
    const auction = auctions.find(a => a.driver?.id === driver.id);
    if (auction) {
      navigate(`/market/auction/${auction.id}`);
    }
  };

  const handleUpdateValues = async () => {
    try {
      const res = await fetch('/api/drivers/update-values', { method: 'POST' });
      const data = await res.json();
      if (res.ok) {
        setSnackbar({ open: true, message: data.message || 'Valores actualizados', severity: 'success' });
        fetchDrivers();
        fetchAuctions();
      } else {
        setSnackbar({ open: true, message: data.error || 'Error actualizando valores', severity: 'error' });
      }
    } catch (err) {
      setSnackbar({ open: true, message: 'Error actualizando valores', severity: 'error' });
    }
  };

  useEffect(() => {
    fetchAuctions();
    const interval = setInterval(fetchAuctions, 10000);
    return () => clearInterval(interval);
  }, [selectedLeague]);

  return (
    <Box sx={{ p: 2, background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%)', minHeight: '100vh' }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography 
          variant="h4" 
          sx={{ 
            fontWeight: 700, 
            color: '#fff',
            textShadow: '0 2px 4px rgba(0,0,0,0.5)',
            background: 'linear-gradient(45deg, #DC0000, #FF4444)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}
        >
          ð Mercado de Pilotos
        </Typography>
        <Box sx={{ display: 'flex', gap: 2 }}>
          <Button
            variant="outlined"
            color="primary"
            onClick={() => { setOpenDrivers(true); fetchDrivers(); }}
            sx={{ 
              fontWeight: 700,
              borderColor: '#DC0000',
              color: '#DC0000',
              '&:hover': {
                borderColor: '#FF4444',
                backgroundColor: 'rgba(220, 0, 0, 0.1)'
              }
            }}
          >
            ð Todos los Pilotos
          </Button>
          <Button
            variant="contained"
            color="warning"
            onClick={handleFinishAllAuctions}
            sx={{ 
              fontWeight: 700,
              background: 'linear-gradient(45deg, #FF9800, #FF5722)',
              '&:hover': {
                background: 'linear-gradient(45deg, #FF5722, #FF9800)'
              }
            }}
          >
            â¡ Finalizar Subastas
          </Button>
          <Button
            variant="contained"
            color="success"
            onClick={handleUpdateValues}
            sx={{
              fontWeight: 700,
              background: 'linear-gradient(45deg, #43A047, #388E3C)',
              '&:hover': {
                background: 'linear-gradient(45deg, #388E3C, #43A047)'
              }
            }}
          >
            ð¹ Actualizar valores
          </Button>
        </Box>
      </Box>

      {/* Modal de todos los pilotos */}
      <Dialog open={openDrivers} onClose={() => setOpenDrivers(false)} maxWidth="lg" fullWidth>
        <DialogTitle sx={{ background: '#1a1a1a', color: '#fff' }}>
          <Typography variant="h6" sx={{ fontWeight: 700 }}>
            ðï¸ Parrilla Completa - {selectedLeague?.name}
          </Typography>
          <IconButton
            aria-label="close"
            onClick={() => setOpenDrivers(false)}
            sx={{ position: 'absolute', right: 8, top: 8, color: '#fff' }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent dividers sx={{ background: '#0a0a0a', p: 3 }}>
          {loadingDrivers ? (
            <Box sx={{ textAlign: 'center', py: 4 }}>
              <Typography sx={{ color: '#fff' }}>ð Cargando parrilla...</Typography>
            </Box>
          ) : driversError ? (
            <Alert severity="error">{driversError}</Alert>
          ) : (
            <Grid container spacing={2}>
              {drivers.length === 0 ? (
                <Grid item xs={12}>
                  <Typography sx={{ color: '#fff', textAlign: 'center', py: 4 }}>
                    No hay pilotos en esta liga.
                  </Typography>
                </Grid>
              ) : (
                drivers.map(driver => (
                  <Grid item xs={12} sm={6} md={4} lg={3} key={driver.id}>
                    <DriverRaceCard
                      driver={driver}
                      showStats={true}
                      isOwned={driver.owner_id > 0}
                      onClick={handleDriverClick}
                    />
                  </Grid>
                ))
              )}
            </Grid>
          )}
        </DialogContent>
      </Dialog>

      {/* Mercado de subastas */}
      {loading && (
        <Box sx={{ textAlign: 'center', py: 4 }}>
          <Typography sx={{ color: '#fff', fontSize: '1.2rem' }}>
            ð Cargando mercado de subastas...
          </Typography>
        </Box>
      )}
      
      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
      
      {auctions.length === 0 && !loading && (
        <Box sx={{ textAlign: 'center', py: 4 }}>
          <Typography sx={{ color: '#fff', fontSize: '1.2rem' }}>
            ð No hay pilotos en subasta actualmente
          </Typography>
          <Typography sx={{ color: '#b0b0b0', mt: 1 }}>
            Los pilotos aparecerÃ¡n automÃ¡ticamente o puedes finalizar las subastas existentes
          </Typography>
        </Box>
      )}

      {auctions.length > 0 && (
        <Box>
          <Typography 
            variant="h5" 
            sx={{ 
              mb: 3, 
              color: '#fff', 
              fontWeight: 700,
              textAlign: 'center',
              textShadow: '0 2px 4px rgba(0,0,0,0.5)'
            }}
          >
            ð Pilotos en Subasta
          </Typography>
          <Grid container spacing={2}>
            {auctions.map(auction => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={auction.id}>
                <DriverRaceCard
                  driver={auction.driver}
                  showStats={true}
                  isOwned={false}
                  onClick={() => navigate(`/market/auction/${auction.id}`)}
                />
              </Grid>
            ))}
          </Grid>
        </Box>
      )}

      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert onClose={() => setSnackbar({ ...snackbar, open: false })} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
}
