import React, { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import AuctionPilotCard from '../components/AuctionPilotCard';
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';
import { useNavigate } from 'react-router-dom';
import Button from '@mui/material/Button';
import { useLeague } from '../context/LeagueContext';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import Avatar from '@mui/material/Avatar';

export default function MarketPage() {
  const { selectedLeague } = useLeague();
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const [openDrivers, setOpenDrivers] = useState(false);
  const [drivers, setDrivers] = useState([]);
  const [loadingDrivers, setLoadingDrivers] = useState(false);
  const [driversError, setDriversError] = useState('');
  const navigate = useNavigate();

  const fetchDrivers = async () => {
    if (!selectedLeague) return;
    setLoadingDrivers(true);
    setDriversError('');
    try {
      const res = await fetch(`/api/drivers?league_id=${selectedLeague.id}`);
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Error cargando pilotos');
      setDrivers(data.drivers || []);
    } catch (err) {
      setDriversError('Error cargando pilotos: ' + err.message);
    } finally {
      setLoadingDrivers(false);
    }
  };

  const fetchAuctions = async () => {
    setLoading(true);
    try {
      const player_id = localStorage.getItem('player_id');
      if (!selectedLeague) {
        setError('Debes seleccionar una liga');
        setLoading(false);
        return;
      }
      const url = `/api/auctions?player_id=${player_id}&league_id=${selectedLeague.id}`;
      const res = await fetch(url);
      const data = await res.json();
      setAuctions(data.auctions || []);
    } catch (err) {
      setError('Error cargando el mercado');
    } finally {
      setLoading(false);
    }
  };

  const handleFinishAllAuctions = async () => {
    try {
      if (!selectedLeague) return;
      const res = await fetch(`/api/auctions/finish-all?league_id=${selectedLeague.id}`, { method: 'POST' });
      if (res.ok) {
        setSnackbar({ open: true, message: 'Todas las subastas han sido finalizadas', severity: 'success' });
        fetchAuctions(); // Recargar la lista
      } else {
        const data = await res.json();
        setSnackbar({ open: true, message: data.error || 'Error finalizando subastas', severity: 'error' });
      }
    } catch (err) {
      setSnackbar({ open: true, message: 'Error finalizando subastas', severity: 'error' });
    }
  };

  useEffect(() => {
    fetchAuctions();
    const interval = setInterval(fetchAuctions, 10000);
    return () => clearInterval(interval);
  }, [selectedLeague]);

  return (
    <Box sx={{ p: 2 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h5">Mercado de Pilotos (Subasta)</Typography>
        <Box sx={{ display: 'flex', gap: 2 }}>
          <Button
            variant="outlined"
            color="primary"
            onClick={() => { setOpenDrivers(true); fetchDrivers(); }}
            sx={{ fontWeight: 700 }}
          >
            Pilotos
          </Button>
          <Button
            variant="contained"
            color="warning"
            onClick={handleFinishAllAuctions}
            sx={{ fontWeight: 700 }}
          >
            FINALIZAR TODAS LAS SUBASTAS (PRUEBA)
          </Button>
        </Box>
      </Box>
      <Dialog open={openDrivers} onClose={() => setOpenDrivers(false)} maxWidth="md" fullWidth>
        <DialogTitle>
          Todos los pilotos de la liga
          <IconButton
            aria-label="close"
            onClick={() => setOpenDrivers(false)}
            sx={{ position: 'absolute', right: 8, top: 8 }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent dividers>
          {loadingDrivers ? (
            <Typography>Cargando pilotos...</Typography>
          ) : driversError ? (
            <Alert severity="error">{driversError}</Alert>
          ) : (
            <Box>
              {drivers.length === 0 ? (
                <Typography>No hay pilotos en esta liga.</Typography>
              ) : (
                drivers.map(driver => (
                  <Box key={driver.id} sx={{ display: 'flex', alignItems: 'center', mb: 2, borderBottom: '1px solid #eee', pb: 1 }}>
                    <Avatar src={driver.image_url ? `/images/${driver.image_url}` : ''} alt={driver.driver_name} sx={{ width: 48, height: 48, mr: 2 }} />
                    <Box>
                      <Typography variant="subtitle1" sx={{ fontWeight: 700 }}>{driver.driver_name}</Typography>
                      <Typography variant="body2" color="#b0b0b0">{driver.team}</Typography>
                    </Box>
                    <Box sx={{ ml: 'auto', textAlign: 'right' }}>
                      <Typography variant="body2" color="#b0b0b0">Valor: <b>{driver.valor.toLocaleString()} €</b></Typography>
                    </Box>
                  </Box>
                ))
              )}
            </Box>
          )}
        </DialogContent>
      </Dialog>
      {loading && <Typography>Cargando...</Typography>}
      {error && <Alert severity="error">{error}</Alert>}
      {auctions.length === 0 && !loading && <Typography>No hay pilotos en subasta.</Typography>}
      {auctions.map(auction => (
        <div key={auction.id} onClick={() => navigate(`/market/auction/${auction.id}`)} style={{ cursor: 'pointer' }}>
          <AuctionPilotCard
            photo={auction.driver?.image_url ? `/images/${auction.driver.image_url}` : ''}
            name={auction.driver?.driver_name || ''}
            team={auction.driver?.team || ''}
            valorBase={auction.valor_base}
            pujaActual={auction.puja_visible}
            tiempoRestante={Math.max(0, Math.floor((new Date(auction.fin_subasta).getTime() - Date.now()) / 1000))}
            numBids={auction.num_bids}
            showBidButton={false}
          />
        </div>
      ))}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert onClose={() => setSnackbar({ ...snackbar, open: false })} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
}
